INSERT INTO `bbs_bbs` VALUES ('1999', '72386', '1291', '工信部向中国广播电视网络有限公司颁发《基础电信业务经营许可证', '', '\n<span style=\"font-size: 14pt; font-family: inherit; line-height: 1.5;\">为<font color=\"#ff2600\">全面推广三网融合工作</font>，进一步扩大电信、广电业务双向进入的深度和广度，促进市场竞争，依中国广播电视网络有\n\n</span><span style=\"font-size: 14pt; font-family: inherit; line-height: 1.5;\">限公司申请，工业和信息化部履行法定程序，于<font color=\"#ff9200\">2016年5月5</font>日向中国广播电视网络有限公司颁发了<font color=\"#00f900\" style=\"font-weight: bold;\">《基础电信业务经营 &nbsp;</font></span><span style=\"font-size: 14pt; font-family: inherit; line-height: 1.5;\"><font color=\"#00f900\" style=\"font-weight: bold;\">许可证》</font>，批准中国广播电视网络有限公司在<span style=\"font-style: italic;\">全国范围内经营互联网国内数据传送业务、国内通信设施服务业务</span>，并允</span><span style=\"font-size: 14pt; font-family: inherit; line-height: 1.5;\">许中国广播电视网络有限公司授权其控股子公司<span style=\"text-decoration: underline;\">中国有线电视网络有限公司</span>在全国范围内经营上述两项基础电信业务。&nbsp;</span><div>\n</div><div><span style=\"font-size: 16pt;\">工业和信息化部</span>副部长<span style=\"font-size: 18pt;\">陈肇雄</span>出席颁证会，指出向中国广播电视网络有限公司颁发<span style=\"font-size: 24pt;\">《基础电信业务经营许可证》</span>具有重\n\n<span style=\"font-family: inherit; line-height: 1.5;\"><span style=\"font-size: 14pt;\">要意义，</span><span style=\"font-size: 36pt;\">并对中国广播电视网络有限公司提出相关要求。</span></span></div><div><span style=\"font-size: 14pt; font-family: inherit; line-height: 1.5;\"><br></span></div><div><ul><li style=\"text-align: right;\"><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">中国广播电视网络有限公司表示，</span><br></li><li style=\"text-align: right;\"><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">将严格遵守相关法律法规，</span><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">&nbsp;&nbsp;</span><br></li><li style=\"text-align: right;\"><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">切实履行企业社会责任和义务，</span><br></li></ul></div><div><span style=\"font-size: 14pt; font-family: inherit; line-height: 1.5;\"></span></div><div><ol><li><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">加快有线电视网络整合，</span><br></li><li><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">积极参与普遍</span><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">服务，</span><br></li><li><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">依法合规经营，</span><br></li><li><span style=\"font-family: inherit; font-size: 14pt; line-height: 1.5;\">创新转型发展。</span></li></ol><div><blockquote>测试测试<br></blockquote><hr>helllo</div></div><div><hr><br></div>', '{\"group_ids\":\"\",\"user_ids\":[\"62210835\"]}', '1', '[{\"url\":\"http:\\/\\/testcloudb.quanshi.com:80\\/ucfserver\\/hddown?fid=NjIyMTA4MzUvOC90ZW1wX25vdGljZV8xNDYyNDM3MzQ2NDMwLmpwZ15eXnRhbmdoZGZzXl5eNTM3MTAwODQ2NTAwYjVjM2Q4ZWJhNTc4NzAwZDNjMjFeXl50YW5naGRmc15eXjE0ODg0$&u=62210835\",\"fid\":\"NjIyMTA4MzUvOC90ZW1wX25vdGljZV8xNDYyNDM3MzQ2NDMwLmpwZ15eXnRhbmdoZGZzXl5eNTM3MTAwODQ2NTAwYjVjM2Q4ZWJhNTc4NzAwZDNjMjFeXl50YW5naGRmc15eXjE0ODg0$\",\"size\":\"14884\",\"embeded\":\"0\"}]', '62210835', '1463477587976', '1463477587976', '1463477587976', '0', 'bbs', 'default', '0', '0', '0');
