INSERT INTO `bbs_bbs` VALUES ('2439', '72386', '1733', '科学家收到50亿年前无线电信号，引发轰动', '', '地球大约于45亿年前，这段比地球历史还要悠久的信号是否证明由外星球传出，印证外星人的存在?这个信号穿越了50亿年来到地球，究竟在传递什么信息?\r<div>　　该信号起初被认为来自地球，而没受到重视，但在2013年，研究人员宣称发现了4段相似的射电脉冲。其放射源头位于50至110亿光年以外，而信号产生时所释放出的能量，相当于太阳4个月释放的总量。虽然源头依然未知，但根据光线传播时间理论，这些信号可能来自于类似中子星的致密星体或其附近，又或者是磁星的超级耀斑。</div><div><a href=\"http://society.nen.com.cn/system/2016/05/31/019141807_01.shtml\" target=\"_blank\">http://society.nen.com.cn/system/2016/05/31/019141807_01.shtml</a><br></div><div><br></div>', '{\"group_ids\":\"\",\"user_ids\":[\"62210835\"]}', '1', '[{\"url\":\"http:\\/\\/testcloud.quanshi.com:80\\/ucfserver\\/hddown?fid=NjIyMTA4MzUvOC90ZW1wX25vdGljZV8xNDY0NzQ4NzY1ODU2LmpwZ15eXnRhbmdoZGZzXl5eMzM3MzQyZWI2ZGM0YzY0MjkwOTRhOTAzZDNiMDIzOGReXl50YW5naGRmc15eXjIxNjQ1MA$&u=62210835\",\"fid\":\"NjIyMTA4MzUvOC90ZW1wX25vdGljZV8xNDY0NzQ4NzY1ODU2LmpwZ15eXnRhbmdoZGZzXl5eMzM3MzQyZWI2ZGM0YzY0MjkwOTRhOTAzZDNiMDIzOGReXl50YW5naGRmc15eXjIxNjQ1MA$\",\"size\":\"216450\",\"embeded\":\"0\"}]', '62210835', '1464748962078', '1464748962078', '1464748962078', '0', 'bbs', 'preview', '0', '0', '0');
