DROP TABLE IF EXISTS `bbs_bbs`;
CREATE TABLE `bbs_bbs` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` bigint(20) unsigned NOT NULL COMMENT '站点ID',
  `board_id` bigint(20) unsigned NOT NULL COMMENT '公告板ID',
  `title` varchar(255) NOT NULL COMMENT '公告标题',
  `description` varchar(255) NOT NULL COMMENT '公告摘要',
  `content` mediumtext NOT NULL COMMENT '公告内容',
  `publish_scope` text NOT NULL,
  `msg_count` int(11) unsigned NOT NULL COMMENT '发送总数',
  `attachments` mediumtext NOT NULL,
  `user_id` bigint(20) unsigned NOT NULL COMMENT '创建人',
  `created_at` bigint(13) unsigned NOT NULL COMMENT '创建时间',
  `publish_at` bigint(13) unsigned NOT NULL COMMENT '发布时间',
  `modified_at` bigint(13) unsigned NOT NULL COMMENT '更新时间',
  `set_timer` bigint(13) unsigned NOT NULL,
  `category` enum('task','form','bbs') NOT NULL DEFAULT 'bbs' COMMENT '公告类型 公告、任务、表单',
  `type` enum('default','preview') NOT NULL DEFAULT 'default',
  `is_deleted` tinyint(1) unsigned NOT NULL COMMENT '是否已被删除0未删除，1已删除',
  `status` tinyint(1) unsigned NOT NULL COMMENT '分析消息总数0未分析完成，1已分析完成',
  `comment_enabled` tinyint(1) unsigned NOT NULL COMMENT '评论类型，1开启评论、0关闭评论',
  PRIMARY KEY (`id`),
  KEY `board_id` (`board_id`),
  KEY `user_id` (`user_id`),
  KEY `created_at` (`created_at`),
  KEY `site_del` (`site_id`,`is_deleted`),
  KEY `type` (`type`),
  KEY `category` (`category`),
  KEY `publish_at` (`publish_at`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=2830 DEFAULT CHARSET=utf8mb4 COMMENT='公告';

